import { useState } from "react";
import CheakMark from "./images/CheakMark.png";
import Nothing from "./images/Nothing.png";
import "./transferCategory.css";

const TransferCategory = () => {
  const [category, setCategory] = useState();
  const transferCategoryItems = [
    "Без пересадок",
    "1 пересадка",
    "2 пересадка",
    "3 пересадка",
  ];
  return (
    <div className="category">
      <div className="category__info">Количество пересадок</div>
      <ul className="category__list">
        {transferCategoryItems.map((item, index) => (
          <li className="category__list__wrapper" key={index} onClick={() => setCategory(index)}>
            <button className="category__list__button">
              <img src={category === index ? CheakMark : Nothing} alt="true" />
            </button>
            <div className="category__list__item">{item}</div>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default TransferCategory;
