import CheakMark from "./images/CheakMark.png";
import Nothing from "./images/Nothing.png";
import "./companiesCategory.css";

const airLineCompanies = [
  { name: "Победа", number: 1 },
  { name: "Red Wings", number: 2 },
  { name: "S7 Airlines", number: 3 },
];

const CompaniesCategory = ({ setCompanies, companies }) => {

  function onHandlerClick(obj) {
    setCompanies((prev) => {
      // Проверяем, содержится ли объект в массиве
      const isSelected = prev.some(item => item.number === obj.number);
      if (isSelected) {
        return prev.filter((item) => item.number !== obj.number);
      } else {
        return [...prev, obj];
      }
    });
  }

  // Функция для проверки, выбрана ли компания
  const isCompanySelected = (company) => {
    return companies.some(item => item.number === company.number);
  };

  return (
    <div className="category">
      <div className="category__info">Компании</div>
      <ul className="category__list">
        {airLineCompanies.map((obj, index) => (
          <li
            className="category__list__wrapper"
            key={obj.number} // Лучше использовать уникальный number
            onClick={() => onHandlerClick(obj)}
          >
            <button className="category__list__button__companies">
              <img
                src={isCompanySelected(obj) ? CheakMark : Nothing}
                alt={isCompanySelected(obj) ? "Выбрано" : "Не выбрано"}
              />
            </button>
            <div className="category__list__item">{obj.name}</div>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default CompaniesCategory;
