import { createSlice } from "@reduxjs/toolkit";
import { dataTickets } from "../../utils/mockApi";

type DataTicketsType = {
  id: number;
  from: "Vladivostok";
  to: "Samara";
  company: "Red Wings";
  price: 19000;
  currency: "RUB";
  time: "3 ч 30 мин";
  startTime: "18:30";
  endTime: "22:00";
  imgUrl: "https://www.columbista.com/data/carrier_logo/119/big-5e25826b419b8.png";
  counterTransfer: 1;
};

const initialState = {
  tickets: dataTickets,
};

export const ticketSlice = createSlice({
  name: "ticket",
  initialState,
  reducers: {
    setTickets(state, action) {
      state.tickets = action.payload;
    },
  },
});

export const { setTickets } = ticketSlice.actions;
export default ticketSlice.reducer;
