import Sort from "../sort/sort";
import TransferCategory from "../transferCategory/transferCategory";
import CompaniesCategory from "../companiesCategory/companiesCategory";
import Tickets from "../tickets/tickets";

import { useState } from "react";
import { dataTickets } from "../../utils/mockApi";

import "./main.css";

const Main = () => {
  const [tickets, setTickets] = useState(dataTickets);
  const [download, setDownload] = useState(3);

  return (
    <>
      <main>
        <TransferCategory />
        <Sort />
        <CompaniesCategory />
        <div className="tickets-container">
          {tickets
            .filter((_, i) => i + 1 <= download)
            .map((item) => (
              <Tickets ticket={item} key={tickets.id} />
            ))}
        </div>
      </main>
      <button className="download-ticket-btn" onClick={() => {setDownload(prev => prev+3)}}>Загрузить еще билеты</button>
    </>
  );
};

export default Main;
