import Sort from "../sort/sort";
import TransferCategory from "../transferCategory/transferCategory";
import CompaniesCategory from "../companiesCategory/companiesCategory";
import Tickets from "../tickets/tickets";

import { useSelector, useDispatch } from "react-redux";
import { downloadingTickets } from "../../redux/Slice";

import { useEffect, useState } from "react";
import { dataTickets, sorting, transferCategorySort} from "../../utils/mockApi";

import "./main.css";

const Main = () => {
  const [companies, setCompanies] = useState([]);
  const [sort, setSort] = useState(0);
  const [transfer, setTransfer] = useState([0]);
  const [tickets, setTicket] = useState(dataTickets)

    useEffect(() => {
    // Комбинируем сортировку и фильтрацию в правильном порядке
    let result = dataTickets;
    
    // 1. Сначала фильтруем по пересадкам
    result = transferCategorySort(transfer, result);
    
    // 2. Затем сортируем
    result = sorting(sort, result);
    
    // 3. Один раз устанавливаем результат
    setTickets(result);
  }, [sort, transfer]);

  const download = useSelector((state) => state.downloading.downloadTickets)
  const dispatch = useDispatch()

  return (
    <>
      <main>
        <TransferCategory transfer={transfer} setTransfer={setTransfer} />
        <Sort sort={sort} setSort={setSort} />
        <CompaniesCategory setCompanies={setCompanies} companies={companies} />
        <div className="tickets-container">
          {ticket
            .filter((_, i) => i + 1 <= download)
            .map((item) => (
              <Tickets ticket={item} key={item.id} />
            ))}
        </div>
      </main>
      <button
        className="download-ticket-btn"
        onClick={() => dispatch(downloadingTickets())}
      >
        Загрузить еще билеты
      </button>
    </>
  );
};

export default Main;
