import { useSelector, useDispatch } from "react-redux";
import { sorting } from "../../redux/Slices/sortSlice"
import "./sort.css";

const Sort = () => {
  const dispatch = useDispatch()
  const sortItems = ["Самый дешевый", "Самый быстрый", "Самый оптимальный"];
  const sort = useSelector((state) => state.sorting.sort)
  return (
    <div className="sort">
      {sortItems.map((item, index) => (
        <div
          className={index === sort ? "active sort__item" : "sort__item"}
          key={index}
          onClick={() => dispatch(sorting(index))}
        >
          {item}
        </div>
      ))}
    </div>
  );
};

export default Sort;
